query:
query {
  StorefrontUsers(where: {_or: [{email: {_eq: "primary@email.com"}}, {phoneNumber: {_eq: "+447883727933"}}]}) {
    id
    email
    firstName
    lastName
    phoneNumber
    platformId
    createdAt
    updatedAt
    Subscriptions {
      id
      status
      createdAt
      updatedAt
      storefrontUserId
      BillingPolicy {
        id
        interval
        intervalCount
      }
      DeliveryPolicy {
        id
        interval
        intervalCount
      }
    }
  }
}
