query:
query {
    StorefrontUsers(
        where: {
            _or: [{email: {_eq: "%!s(<nil>)"}}, {phoneNumber: {_eq: "%!s(<nil>)"}}, {phoneNumber: {_eq: "+447888727888"}}]
        }
    ) {
        id
        email
        firstName
        lastName
        phoneNumber
        platformId
        createdAt
        updatedAt
    }
}
