query:
query {
    Subscriptions(
        where: {
            StorefrontUser: {id: {_eq: "18038b73-a9db-4809-a5c9-054db8c9ee1f"}}
        },
        order_by: {createdAt: desc}
    ) {
        id
        platformId
        status
        createdAt
        nextBillingDate
        storefrontUserId
        cyclesCompleted
        cancelledAt
        currencyCode
        BillingPolicy {
            id
            interval
            intervalCount
            isMaxCycleV2
            maxCycles
            minCycles
            createdAt
            updatedAt
        }
        DeliveryPolicy {
            id
            interval
            intervalCount
            isMaxCycleV2
            maxCycles
            minCycles
            createdAt
            updatedAt
        }
        SubscriptionLines(where: {removedAt: {_is_null: true}}) {
            id
            priceWithoutDiscount
            quantity
            sellingPlanId
            isPrepaid
            ordersRemaining
            ProductVariant {
                title
                Product {
                    title
                }
            }
        }
    }
}
